#############################################################################
# DATABASE FUNCTIONS
#############################################################################
def sql_connection():
    try:
        con = sqlite3.connect('database.db')
        return con
    except Error:
        print(Error)
def getID(con):
    newid=0
    cursorObj = con.cursor()
    cursorObj.execute('SELECT * FROM aicynthia ORDER BY id DESC')
    rows = cursorObj.fetchall()
    for row in rows:
        newid = int(row[0])+1
        break
    return newid
def getID2(con):
    newid=0
    cursorObj = con.cursor()
    cursorObj.execute('SELECT * FROM faces ORDER BY id DESC')
    rows = cursorObj.fetchall()
    for row in rows:
        newid = int(row[0])+1
        break
    return newid
def findAnswer(con,question):
    qx = question.split()
    cursorObj = con.cursor()
    cursorObj.execute('SELECT * FROM aicynthia ORDER BY id DESC')
    rows = cursorObj.fetchall()
    bc = 0
    answer = ""
    for row in rows:
        lc = 0
        q = row[1]
        a = row[2]
        for w in qx:
            lc += q.count(w)
        if lc > bc:
            answer = a
            bc = lc
        if bc == len(qx):
            break
    return answer
def sql_table(con):
    cursorObj = con.cursor()
    cursorObj.execute("CREATE TABLE aicynthia(id integer PRIMARY KEY, question text, answer text)")
    con.commit()
def sql_table2(con):
    cursorObj = con.cursor()
    cursorObj.execute("CREATE TABLE faces(id integer PRIMARY KEY, name text, path text)")
    con.commit()
def checkDB(con,question):
    cursorObj = con.cursor()
    cursorObj.execute("SELECT * FROM aicynthia WHERE question='"+question+"'")
    rows = cursorObj.fetchall()
    for row in rows:
        if question == row[1]:
            return True
    return False
def checkDB2(con,name):
    cursorObj = con.cursor()
    cursorObj.execute("SELECT * FROM faces WHERE name='"+name+"'")
    rows = cursorObj.fetchall()
    for row in rows:
        if name == row[1]:
            return True
    return False
def selectFaces(con):
    cursorObj = con.cursor()
    cursorObj.execute("SELECT * FROM faces")
    rows = cursorObj.fetchall()
    for row in rows:
        name = row[1]
        path = row[2]
        print(name)
        print(path)
def getFacesPaths(con):
    fp = []
    cursorObj = con.cursor()
    cursorObj.execute("SELECT * FROM faces")
    rows = cursorObj.fetchall()
    for row in rows:
        name = row[1]
        path = row[2]
        fp.append([name,path])
    return fp
def sql_update(con,question,answer):
    cursorObj = con.cursor()
    cursorObj.execute("UPDATE aicynthia SET answer = '"+answer+"' WHERE question = '"+question+"'")
    con.commit()
def sql_update2(con,name,path):
    cursorObj = con.cursor()
    cursorObj.execute("UPDATE faces SET path = '"+path+"' WHERE name = '"+name+"'")
    con.commit()
def deleteFromFaces(con,name):
    cursorObj = con.cursor()
    cursorObj.execute("DELETE FROM faces WHERE name='"+name+"'")
    con.commit()
def insertDB(con,question,answer):
    cdb = checkDB(con,question)
    if cdb == True:
        sql_update(con,question,answer)
    else:
        id =getID(con)
        cursorObj = con.cursor()
        cursorObj.execute("INSERT INTO aicynthia VALUES("+str(id)+", '"+question+"', '"+answer+"')")
        con.commit()
def insertDB2(con,name,path):
    cdb = checkDB2(con,name)
    if cdb == True:
        sql_update2(con,name,path)
    else:
        id =getID2(con)
        cursorObj = con.cursor()
        cursorObj.execute("INSERT INTO faces VALUES("+str(id)+", '"+name+"', '"+path+"')")
        con.commit()
def deleteFromDB(con,id):
    cursorObj = con.cursor()
    cursorObj.execute("DELETE FROM aicynthia WHERE id = '"+str(id)+"'")
    con.commit()
def sql_fetch(con):
    cursorObj = con.cursor()
    cursorObj.execute('SELECT * FROM aicynthia')
    rows = cursorObj.fetchall()
    for row in rows:
        print(row)
#############################################################################
# RECORD MIC
#############################################################################
def recordMic(dur=5):        
    r = sr.Recognizer()
    with sr.Microphone() as source:
        try:
            tnow = datetime.datetime.now()
            current_time = tnow.strftime("%H:%M:%S")
            #print("["+colored(current_time,"blue")+"] "+colored("Recording Start for "+str(dur)+" Seconds...","green",attrs=['bold']))
            print("["+colored(current_time,"blue")+"] "+colored("Recording Start...","green",attrs=['bold']))
            text = ""
            while True:
                audio = r.record(source,duration=5)
                try:
                    pretext = r.recognize_google(audio,language="el-GR")
                except:
                    pretext = ""
                    pass    
                #print(pretext)
                if pretext.strip().replace(" ","") == "":
                    break
                else:
                    text += " "+pretext
            #text = r.recognize_google(audio,language="el-GR")
            #print("Final Text : "+text)
            if text[0] == " ":
                text = text[1:]
            tnow = datetime.datetime.now()
            current_time = tnow.strftime("%H:%M:%S")
            print("["+colored(current_time,"blue")+"] "+colored("Recorded Text : ","green",attrs=['bold'])+text)
            return text
        except:
            tnow = datetime.datetime.now()
            current_time = tnow.strftime("%H:%M:%S")
            print("["+colored(current_time,"blue")+"] "+colored("Recording Error","red",attrs=['bold']))
            pass
            return False
